mapper.select:
        SELECT


--         级联表的字段

        FROM meishi  a



                and a.id in

                #{item}



                and a.meishi_name like CONCAT('%',#{params.meishiName},'%')


                and a.meishi_types = #{params.meishiTypes}


                  and a.meishi_new_money >= #{params.meishiNewMoneyStart}   


                  and a.meishi_new_money <= #{params.meishiNewMoneyEnd}   


                and a.meishi_content like CONCAT('%',#{params.meishiContent},'%')


                  and a.meishi_delete >= #{params.meishiDeleteStart}   


                  and a.meishi_delete <= #{params.meishiDeleteEnd}   


                and a.meishi_delete = #{params.meishiDelete}



        order by a.${params.sort} ${params.order}
